mapper.select:
select * from( select t1.*, nvl(t2.name, '系统') preMenu, v1.name creatorName, v2.name updatorName from t_sys_resource t1 left join t_sys_resource t2 on t1.pid = t2.id left join t_sys_user v1 on t1.creator = v1.id left join t_sys_user v2 on t1.updator = v2.id order by t1.sort)
order by ${SORT}
${DIR}

select 1 num from dual where exists( select name from t_sys_resource t

	and t.name = #{name} and t.menulevel = #{menuLevel}

		and t.id != #{id}


)

select 1 num from dual where exists( select sort from t_sys_resource t 

	and t.pid = #{pid} and t.sort = #{sort}

		and t.id != #{id}


)
